import React from 'react'
import styled from "styled-components"
import { useFormik } from "formik"
import * as yup from "yup"
import { useState } from 'react'
import { v4 as uuid } from "uuid"
import api from "../../../src/api/Contact"
import { useHistory } from 'react-router-dom'



const RegiContainer = styled.div`
display:flex;
width:100%;
position:absolute;
`
const LeftDiv = styled.div`
height:100vh;
width:33%;
background: url("https://job-static.hirist.com/V2/static/media/login_bkg.9cb0156b.jpg");
background-position: top;
position: relative;
background-position-x: 0;
color:#fff;
`
const LogoImg = styled.img`
cursor: pointer;
margin:10vh auto;
position: relative;
margin-left:30%;
align-items:center;
`
const LeftHeading = styled.h2`
word-spacing:1px;
margin:1% 7%;
text-align:center;
`
const PDetail = styled.p`
font-weight: 400;
line-height: 1.69;
font-size: 16px;
text-align:center;
`
const CompanyList = styled.div`
font-size: 16px;
font-weight: 400;
line-height: 1.69;
text-align: center;
margin-top:27vh;
p {
    display: block;
    -webkit-margin-before: 1em;
    margin-block-start: 1em;
    -webkit-margin-after: 1em;
    margin-block-end: 1em;
    -webkit-margin-start: 0;
    margin-inline-start: 0;
    -webkit-margin-end: 0;
    margin-inline-end: 0;
}
`

const CompanyLogos = styled.div`
display:flex;
`
const RightDiv = styled.div`
width:50%;
height:100vh;
position:relative;
margin-left:5%;
`

const TopBtn = styled.button`
width: 27%;
height: 48px;
position: absolute;
right: 130px;
top: 50px;
margin: 0;
padding: 11px 12px 11px 14px;
border-radius: 24px;
border: 1px solid #fceecf;
background-color: #fffbf1;
cursor: pointer;
outline: none;
`
const BtnImg = styled.img`
width:20%;
position:relative;
left:-10%;
max-height:90%;
`

const SpanBtnText = styled.span`
margin: -3px 13px 3px 7px;
font-family: Nunito Sans;
font-size: 17px;
font-weight: 500;
font-stretch: normal;
font-style: normal;
line-height: normal;
letter-spacing: normal;
color: #e9630c;
position:relative;
top:-12%;
`
const AuthCont = styled.form`
position:relative;
    margin: 110px auto 0;
    width: 65%;

`
const AuthHeading = styled.h1`
font-weight:170;
font-size:45px;
word-spacing:6px;
`
const AuthB = styled.b`
    font-size: 26px;
    color: #000;
    display: block;
    font-weight: 700;
    
`

const FieldContainer = styled.div`
margin-top: 25px;
width: 100%;
`
const FieldLabel = styled.label`
display: block;
opacity: .8;
font-size: 14px;
font-weight: 700;
color: #000;
margin-bottom: 5px;
`
const FieldInp = styled.input`
width: 100%;
display: block;
height: 40px;
border-radius: 4px;
border: 2px solid #e6e6e6;
background-color: #fff;
font-size: 16px;
outline: none;
padding: 10px 10px;
`
const SubBtn = styled.button`
    position: relative;
    margin-top: 45px;
    width: 100%;
    height: 60px;
    border-radius: 8px;
    background-color: #e9630c;
    font-size: 20px;
    font-weight: 400;
    color: #fff;
    border: 0;
    outline: 0;
`
const PSpan = styled.span`
    font-weight: 700;
    color: #e9630c;
    cursor: pointer;
`
const FieldContainer2 = styled.div`
width:100%;
display:flex;
flex-direction:column;
`

const FieldError = styled.span`
color:#db2222;
font-size:11px;
min-height:10px;
`


export const LoginForm = () => {
    const history = useHistory();
    const [userData, setUserData] = useState([])
    const [loginData, setLoginData] = useState()

    const PASSWORD_REGEX = /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{8,}$/;

    const validationSchema = yup.object({
        email: yup.string().email("please Enter a valid email address").required(),
        password: yup.string().matches(PASSWORD_REGEX, "Please enter a strong password").required(),

    })

    const onSubmit = async (value) => {
        setLoginData(value)
        const response = await api.get("/userLogin")
        const { data } = response
        setUserData(data)
        checkValidation(userData, loginData)
    }

    const checkValidation = (userData, loginData) => {
        userData.map((e) => {
            if (e.email === loginData.email && e.password === loginData.password) {

                return history.push('/Company')

            }// } else {
            //     return alert("Enter a valid id or password")

            // }
        })
    }

    const formik = useFormik({
        initialValues: { email: "", password: "", },
        validateOnBlur: true,
        onSubmit,
        validationSchema: validationSchema,
    })

    console.log("Error", formik.errors);
    const { email, password } = formik.errors
    return (
        <RegiContainer>
            <LeftDiv>
                <LogoImg onClick={() => history.push("/")} src="data:image/png;base64," alt="logo">
                </LogoImg>
                <LeftHeading>
                    Choose a job you love, and you never have to work a day in your life
                </LeftHeading>
                <PDetail>
                    - Confucius
                </PDetail>
                <CompanyList>
                    <p>Companies you might love to work with</p>
                </CompanyList>
                <CompanyLogos>
                    <a style={{ marginLeft: "6%" }}>
                        <img src="https://hirist-logos.s3.ap-south-1.amazonaws.com/Amazon+Login.png" alt="Amazon" />

                    </a>
                    <a style={{ marginLeft: "6%" }}>
                        <img src="https://hirist-logos.s3.ap-south-1.amazonaws.com/Amex+Login.png" alt="Amazon" />
                    </a>
                    <a style={{ marginLeft: "6%" }}>
                        <img src="https://hirist-logos.s3.ap-south-1.amazonaws.com/Zycus+Login.png" alt="Amazon" />
                    </a>
                </CompanyLogos>

            </LeftDiv>
            <RightDiv>

                <TopBtn>
                    <BtnImg src="data:image/png;base64," />
                    <SpanBtnText>Download App</SpanBtnText>
                </TopBtn>

                <AuthCont onSubmit={formik.handleSubmit}>
                    <AuthHeading>
                        <AuthB>Welcome Guest,</AuthB>
                        Sign up for Hirist
                    </AuthHeading>

                    <FieldContainer>
                        <FieldContainer2>
                            <FieldLabel for="email" >Email Address</FieldLabel>
                            <FieldInp type="text" placeholder="you@example.com" name="email"
                                value={formik.values.email}
                                onBlur={formik.handleBlur}
                                onChange={formik.handleChange} />
                            <FieldError>{formik.touched.email && formik.errors.email ? email : ""}</FieldError>
                        </FieldContainer2>
                    </FieldContainer>
                    <FieldContainer>
                        <FieldContainer2>
                            <FieldLabel for="password" >Password</FieldLabel>
                            <FieldInp type="password" placeholder="Enter 8 character or more" name="password"
                                value={formik.values.password}
                                onChange={formik.handleChange}
                                onBlur={formik.handleBlur}
                            />
                            <FieldError>{formik.touched.password && formik.errors.password ? password : ""}</FieldError>
                        </FieldContainer2>
                    </FieldContainer>

                    <SubBtn type="submit">
                        Continue
                    </SubBtn>
                </AuthCont>

                <p style={{
                    marginLeft: "0",
                    textAlign: "center",
                    fontSize: "16px",
                    fontWeight: "400",
                    color: "#7e7e7e"
                }}>Don't have an account?
                    <PSpan onClick={() => {
                        history.push('/Regiform')
                    }}>Sign Up</PSpan></p>
            </RightDiv>
        </RegiContainer>
    )
}
